import React, { useState, useEffect } from 'react';
import { Form, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import base_url from '../api/bootapi';
import { Button, Container, FormGroup, Input, Label } from 'reactstrap';

const UpdateCourse = () => {
  const { id } = useParams(); // Get the id parameter from the URL
  const [course, setCourse] = useState({
    title: '',
    description: ''
  });

  useEffect(() => {
    // Fetch the course details using the id from the URL
    axios.get(`${base_url}/courses/${id}`)
      .then(response => {
        setCourse(response.data);
      })
      .catch(error => {
        toast.error('Failed to fetch course details');
      });
  }, [id]);

  const handleChange = e => {
    const { name, value } = e.target;
    setCourse(prevCourse => ({
      ...prevCourse,
      [name]: value
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    axios.put(`${base_url}/courses`, course)
      .then(response => {
        toast.success('Course updated successfully');
        // Handle any additional actions after updating the course
      })
      .catch(error => {
        toast.error('Failed to update course');
      });
  };

  return (
    <div>
      <h2>Update Course</h2>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="title">Title</Label>
          <Input type="text"id="title" value={course.title} onChange={handleChange}/>
        </FormGroup>

        <FormGroup>
          <Label for="description">Description</Label>
          <Input type="textarea" id="description" value={course.description} onChange={handleChange}/>
        </FormGroup>
        <Container>
        <Button type="submit" className="btn btn-primary">Update</Button>
        </Container> 
      </Form>
    </div>
  );
};

export default UpdateCourse;
