import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const UpdateCourse = () => {
  const { id } = useParams(); // Get the id parameter from the URL
  const [course, setCourse] = useState({
    title: '',
    description: ''
  });

  useEffect(() => {
    // Fetch the course details using the id from the URL
    axios.get(`${base_url}/courses/${id}`)
      .then(response => {
        setCourse(response.data);
      })
      .catch(error => {
        toast.error('Failed to fetch course details');
      });
  }, [id]);

  const handleChange = e => {
    // Update the course state when input fields change
    const { name, value } = e.target;
    setCourse(prevCourse => ({
      ...prevCourse,
      [name]: value
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    // Send a PUT request to update the course details
    axios.put(`${base_url}/courses/${id}`, course)
      .then(response => {
        toast.success('Course updated successfully');
        // Handle any additional actions after updating the course
      })
      .catch(error => {
        toast.error('Failed to update course');
      });
  };

  return (
    <div>
      <h2>Update Course</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input type="text" className="form-control" id="title" name="title" value={course.title} onChange={handleChange}/>
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea className="form-control" id="description" name="description" value={course.description} onChange={handleChange}></textarea>
        </div>
        <button type="submit" className="btn btn-primary">Update</button>
      </form>
    </div>
  );
};

export default UpdateCourse;
