import React, { useState, useEffect } from 'react';
import { Form, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import base_url from '../api/bootapi';
import { Button, Container, FormGroup, Input, Label } from 'reactstrap';

const UpdateCourse = () => {
const { id } = useParams();

  useEffect(() => {
    // Fetch the course details using the id from the URL
    axios.get(`${base_url}/courses/${id}`)
      .then(response => {
        setCourse(response.data);
      })
      .catch(error => {
        toast.error('Failed to fetch course details');
      });
  }, [id]);

const[course, setCourse]=useState({});

    const handleForm=(e)=>{
        postDataToServer(course);
        e.preventDefault();
    };

    const postDataToServer=(data)=>{
        axios.post(`${base_url}/courses`,data).then(
            (response)=>{
                console.log(response.data);
                toast.success("Course added successfully");

            },
            (error)=>{
                console.log(error.data); 
                toast.error("Error in adding course");
            } 
        );
    };

    return (
    <div>
        <h1>Update Course Details</h1>
        <Form onSubmit={handleForm}>
            <FormGroup>
                <Label for="userId">Course Id</Label>
                <Input id="userId" name="userId" type="number" 
                onChange={(e)=>{setCourse({...course,id:e.target.value});}}/>
            </FormGroup>

            <FormGroup>
                <Label for="title">Course Title</Label>
                <Input id="title" type="text" 
                onChange={(e)=>{setCourse({...course,title:e.target.value});}}/>
            </FormGroup>

            <FormGroup>
                <Label for="description">Course Description</Label>
                <Input id="description" type="textarea" style={{height:100}} 
                onChange={(e)=>{setCourse({...course,description:e.target.value});}}/>   
            </FormGroup>

            <Container className="text-center">
                <Button type="submit" color="success">Update</Button>
                <Button type="reset" color="warning">Clear</Button>
            </Container>
        </Form>
    </div>
    )
}


export default UpdateCourse;
