import axios from "axios";
import React, { useState } from "react";
import { Card,CardBody,CardSubtitle,CardText,Button, Container } from "reactstrap";
import base_url from "../api/bootapi";
import { toast } from "react-toastify";

const Course=({course,update})=>{

    const [updatedCourse, setUpdatedCourse] = useState({
        title: course.title,
        description: course.description
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedCourse(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const updateCourse = (id) => {
        axios.put(`${base_url}/courses/${id}`, updatedCourse)
            .then((response) => {
                toast.success("Course updated");
                // Optionally, you can reload the course list or update the course state
                // based on your application's requirements
            })
            .catch((error) => {
                toast.error("Failed to update course");
            });
    };

    return(
        <div>
            <Card>
                <CardBody className="text-center">
                    <CardSubtitle className="font-weight-bold">{course.title}</CardSubtitle>
                    <CardText>{course.description}</CardText>
                    <Container>
                        <Button color="danger" onClick={() => deleteCourse(course.id)}>Delete</Button>{'   '}
                        <Button color="success ml-3" onClick={() => updateCourse(course.id)}>Update</Button>
                    </Container>
                </CardBody>
            </Card>
        </div>
    );
};

export default Course;
