import axios from "axios";
import React, { useState } from "react";
import { Card,CardBody,CardSubtitle,CardText,Button, Container,Label,FormGroup,Form, Input } from "reactstrap";
import base_url from "../api/bootapi";
import { toast } from "react-toastify";

const Course=({course,update})=>{

    const [updatedCourse, setUpdatedCourse] = useState({
        id: course.id,
        title: course.title,
        description: course.description
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedCourse(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const updateCourse = () => {
        axios.put(`${base_url}/courses`, updatedCourse).then(
            (response) => {
                toast.success("Course updated");
                update(updatedCourse); // Assuming this function updates the course list after an update
            },
            (error) => {
                toast.error("Course not updated");
            }
        );
    };

    const deleteCourse=(id)=>{
        axios.delete(`${base_url}/courses/${id}`).then(
            (response)=>{
                toast.success("Course deleted");
                update(id);
            },
            (error)=>{
                toast.error("Course not deleted");
            }
        );
    };

    return(
        <div>
            <Card>
                <CardBody className="text-center">
                    <CardSubtitle className="font-weight-bold">{course.title}</CardSubtitle>
                    <CardText>{course.description}</CardText>
                    <Container>
                        <Button color="danger" onClick={() => deleteCourse(course.id)}>Delete</Button>{'   '}
                        <Button color="success ml-3" onClick={updateCourse}>Update</Button>
                    </Container>
                    <Form className="mt-3">
                        <FormGroup>
                            <Label for="title">Title</Label>
                            <Input type="text" name="title" id="title" value={updatedCourse.title} onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">Description</Label>
                            <Input type="textarea" name="description" id="description" value={updatedCourse.description} onChange={handleChange} />
                        </FormGroup>
                    </Form>
                </CardBody>
            </Card>
        </div>
    );

};

export default Course;
