import React from 'react';
import './App.css';
import { ToastContainer, toast } from 'react-toastify';
import { Button } from 'reactstrap';

function App() {

  const btnHandle = () =>{
    toast("This is a simple message");
    toast.success("Success",{
      position:"top-center",
    });
  };

  return (
    <div>
        <h1>This is a bootstrap component</h1>
        <Button color="danger" outline onClick={btnHandle}>Click</Button>
        <ToastContainer/>
    </div>
  );
}

export default App;
