import axios from "axios";
import { Card,CardBody,CardSubtitle,CardText,Button, Container } from "reactstrap";
import base_url from "../api/bootapi";
import { toast } from "react-toastify";
import { useState } from "react";

const Course=({course,update})=>{

    const deleteCourse=(id)=>{
        axios.delete(`${base_url}/courses/${id}`).then(
            (response)=>{
                toast.success("Course deleted");
                update(id);
            },
            (error)=>{
                toast.error("Course not deleted");
            }
        );
    };


    const updateCourse = () => {

        // Assuming you have a state to hold the updated course data
        const updatedCourseData = {
            id: course.id, // Assuming course.id exists
            title: "Updated Title", // Update with your updated title
            description: "Updated Description" // Update with your updated description
        };

        axios.put(`${base_url}/courses`, updatedCourseData).then(
            (response) => {
                toast.success("Course updated");
                // Optionally, you can call the update function here to refresh the course list
                // update(response.data.id);
            },
            (error) => {
                toast.error("Course not updated");
            }
        );
    };

    return(
        <div>
            <Card>
                <CardBody className="text-center">
                    <CardSubtitle className="font-weight-bold">{course.title}</CardSubtitle>
                    <CardText>{course.description}</CardText>
                    <Container>
                        <Button color="danger" onClick={()=>{deleteCourse(course.id);}}>Delete</Button>{'   '}
                        <Button color="success ml-3" onClick={updateCourse}>Update</Button>
                    </Container>
                </CardBody>
            </Card>
        </div>
    )
};

export default Course;
